package com.github.telegramdrive.core;

import org.junit.Test;

import java.io.File;
import java.io.FileWriter;
import java.io.IOException;

import static org.junit.Assert.*;

public class TgDriveTest {

	@Test
	public void createTestFile() {
		FileWriter writer = null;
		try {
			writer = new FileWriter(new File("./localfile.txt"));
			for (int i = 0; i < 200; i++) {
				writer.append(String.valueOf(i)).append(":    ").append("").append("\n");
				writer.flush();
			}
		} catch (IOException e) {
			e.printStackTrace();
		} finally {
			if (writer != null) {
				try {
					writer.close();
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
		}

	}

	@Test
	public void upload() {
		TgDrive drive = new TgDrive();
		drive.upload("./iTunes64Setup.exe", "/iTunes64Setup5.exe");
		drive.upload("./iTunes64Setup.exe", "/iTunes64Setup6.exe");
		drive.upload("./iTunes64Setup.exe", "/iTunes64Setup7.exe");
	}

	@Test
	public void download() {
		TgDrive drive = new TgDrive();
		drive.download("/iTunes64Setup7.exe", "./diTunes64Setup7.exe");
		drive.download("/iTunes64Setup6.exe", "./diTunes64Setup6.exe");
		drive.download("/iTunes64Setup5.exe", "./diTunes64Setup5.exe");
	}
}